mapper.update:
UPDATE review2
   SET review_score2   = #{review_score2},
       review_content2 = #{review_content2},
       review_file2    = #{review_file2, jdbcType=VARCHAR},
       review_writer2	=  #{review_writer2, jdbcType=VARCHAR}
 WHERE review_id2 = #{review_id2}

mapper.select:
SELECT COUNT(*)
  FROM review2 
 WHERE con_id = #{con_id}

SELECT review_file2
  FROM review2
 WHERE review_id2 = #{review_id2}
